clc;clear;format long g;more off;function ans = dichtef(k)  ans = 1/243 .* ((k.^3)/3.-9.*k.^2.+81.*k);endfunctionfunction ans = verteilungf(k)  ans = 1/243 .* (9.-k).^2;endfunctionfunction ans = erwartungf(x)  ans = x  .* verteilungf(x);endfunctionfunction ans = varianzf(x)  global erwartung;  ans = (x .- erwartung).^2 .* verteilungf(x);endfunctionfplot(@dichtef, [0 9], 'b');hold onfplot(@verteilungf, [0 9], 'r');hold offtitle('Darstellung der Verteilungs und Dichtefunktion')xlabel('Arbeitszeit in Stunden')ylabel('Wahrscheinlichkeit')legend({'Dichtefunktion','Verteilungsfunktion'},'Location','EastOutside')axis autogrid## Berechnen Sie Erwartungswert und ## Standardabweichung der Zeitdauer X. global erwartung;disp("Aufgabe 1) \n")erwartung = quad("erwartungf", 0, 9)varianz = quad("varianzf", 0, 9)standard_abw = sqrt(varianz)erwartungswert_rechnung = erwartung * 80 + 30standard_abw_rechnung = standard_abw * 80## Aufgabe 2## Für die Ausführungszeit X in Stunden einer bestimmten Arbeit wird die## folgende Dichtefunktion angenommen: f(x) = C ( 9 – x )², 0 < x < 9, und f(x) = 0 sonst. ## Aufgabe 3## Die Längenmessung von 10 Schraubenprintf('\nAufgabe 3)\n\n');messungen = [10 8 9 10 11 11 9 12 8 12];n = length(messungen);varianz = 4; % 4 mm^2standardabw = sqrt(varianz);mittelwert = 1/length(messungen) * sum(messungen); # gesuchtgamma = 0.95;omega = (gamma + 1) / 2;c = stdnormal_inv(omega);interval = ((c * standardabw) / sqrt(n));printf('Mit der Wahrscheinlichkeit von %d%% ist mue zwischen %.4d und %.4d \n', gamma*100, mittelwert - interval, mittelwert + interval);